import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./ViewPOC.css";

const ViewPOC = () => {
  const [departments, setDepartments] = useState([]);
  const [pocs, setPocs] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { departmentId } = useParams(); // Get departmentId from URL

  useEffect(() => {
    fetch("/api/departments")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched departments:", data);
        setDepartments(data);
      })
      .catch((error) => console.error("Error fetching departments:", error));
  }, []);

  const handleDepartmentClick = (departmentId) => {
    console.log("Navigating to department:", departmentId);
    navigate(`/view-poc/${departmentId}`);
  };

  // Fetch POCs and appointments when departmentId is available
  useEffect(() => {
    if (departmentId) {
      setLoading(true);
      fetch(`/api/appointments/${departmentId}`)
        .then((res) => res.json())
        .then((data) => {
          setPocs(data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching POCs and appointments:", error);
          setLoading(false);
        });
    }
  }, [departmentId]);

  return (
    <div className="view-poc">
      {!departmentId ? (
        <>
          <h1>Select a Department</h1>
          {departments.map((dept) => (
            <button
              key={dept.departmentId}
              onClick={() => handleDepartmentClick(dept.departmentId)}
            >
              {dept.Value_name}
            </button>
          ))}
        </>
      ) : (
        <>
          <h1>POC Details</h1>
          {loading ? (
            <p>Loading POCs and appointments...</p>
          ) : (
            <div>
              {pocs.length > 0 ? (
                pocs.map((poc) => (
                  <div key={poc.POC_ID} className="poc-card">
                    <h2>{poc.POC_Name}</h2>
                    <p>Specialization: {poc.Specialization}</p>
                    <p>Contact: {poc.Contact_Number}</p>
                    <p>Email: {poc.Email}</p>
                    <h3>Available Appointments:</h3>
                    <ul>
                      {poc.appointments.map((appointment) => (
                        <li key={appointment.Slot_ID}>
                          {appointment.Schedule_Date} |{" "}
                          {appointment.Start_Time} - {appointment.End_Time} |{" "}
                          {appointment.appointments_per_slot} slots available
                        </li>
                      ))}
                    </ul>
                  </div>
                ))
              ) : (
                <p>No POCs or appointments available for this department.</p>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ViewPOC;
