import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./ViewPOC.css";

const ViewPOC = () => {
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/api/departments") // Replace with actual endpoint to fetch departments
      .then((res) => res.json())
      .then((data) => setDepartments(data))
      .catch((error) => console.error("Error fetching departments:", error));
  }, []);

  const handleDepartmentClick = (departmentId) => {
    navigate(`/view-poc/${departmentId}`);
  };

  return (
    <div className="view-poc">
      <h1>Select a Department</h1>
      {departments.map((dept) => (
        <button key={dept.id} onClick={() => handleDepartmentClick(dept.id)}>
          {dept.name}
        </button>
      ))}
    </div>
  );
};

export default ViewPOC;
