import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./POCAppointments.css";

const POCAppointments = () => {
  const { departmentId } = useParams();
  const [pocDetails, setPocDetails] = useState([]);

  useEffect(() => {
    fetch(`/api/pocs/${departmentId}`)
      .then((res) => res.json())
      .then((data) => setPocDetails(data))
      .catch((error) => console.error("Error fetching POC details:", error));
  }, [departmentId]);

  return (
    <div className="poc-appointments">
      <h1>POC Appointments</h1>
      {pocDetails.map((poc) => (
        <div key={poc.POC_ID} className="poc-card">
          <h2>{poc.POC_Name} ({poc.Specialization})</h2>
          <p>Contact: {poc.Contact_Number} | Email: {poc.Email}</p>
          <h3>Appointments:</h3>
          <ul>
            {poc.appointments.map((appt) => (
              <li key={appt.Slot_ID}>
                {appt.Schedule_Date} | {appt.Start_Time} - {appt.End_Time} | Slots Left: {appt.appointments_per_slot}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default POCAppointments;
