import React, { useState } from "react";
import "./BookAppointment.css";

function BookAppointment() {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [userData, setUserData] = useState(null);
  const [newUser, setNewUser] = useState(false);
  const [name, setName] = useState("");
  const [location, setLocation] = useState("");
  const [appointmentType, setAppointmentType] = useState("");
  const [departmentId, setDepartmentId] = useState("");
  const [pocId, setPocId] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [departments, setDepartments] = useState([]);
  const [pocs, setPocs] = useState([]);
  const [dates, setDates] = useState([]);
  const [times, setTimes] = useState([]);

  // Fetch user data using fetch API
  const fetchUserData = async () => {
    try {
      const response = await fetch("/api/getUserData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ phoneNumber }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data) {
          setUserData(data);
        } else {
          setNewUser(true);
        }
      } else {
        console.error("Error fetching user data:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // Save new user
  const saveNewUser = async () => {
    try {
      const response = await fetch("/api/insertUser", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ phoneNumber, name, location }),
      });

      if (response.ok) {
        const data = await response.json();
        setUserData(data);
        setNewUser(false);
      } else {
        console.error("Error saving new user:", response.statusText);
      }
    } catch (error) {
      console.error("Error saving new user:", error);
    }
  };

  // Fetch departments using fetch API
  const fetchDepartments = async () => {
    try {
      const response = await fetch("/api/getDepartments");
      if (response.ok) {
        const data = await response.json();
        setDepartments(data);
      } else {
        console.error("Error fetching departments:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  // Fetch POCs for selected department
  const fetchPOCs = async (departmentId) => {
    try {
      const response = await fetch(`/api/getPOCs/${departmentId}`);
      if (response.ok) {
        const data = await response.json();
        setPocs(data);
      } else {
        console.error("Error fetching POCs:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching POCs:", error);
    }
  };

  // Fetch available dates for selected POC
  const fetchAvailableDates = async (pocId) => {
    try {
      const response = await fetch(`/api/getAvailableDates/${pocId}`);
      if (response.ok) {
        const data = await response.json();
        setDates(data);
      } else {
        console.error("Error fetching available dates:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching available dates:", error);
    }
  };

  // Fetch available times for selected POC and date
  const fetchAvailableTimes = async (pocId, date) => {
    try {
      const response = await fetch(`/api/getAvailableTimes/${pocId}/${date}`);
      if (response.ok) {
        const data = await response.json();
        setTimes(data);
      } else {
        console.error("Error fetching available times:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching available times:", error);
    }
  };

  // Handle booking confirmation
  const handleConfirm = async () => {
    try {
      const appointmentData = {
        userId: userData.User_ID,
        appointmentType,
        pocId,
        appointmentDate,
        appointmentTime,
      };
      const response = await fetch("/api/bookAppointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(appointmentData),
      });

      if (response.ok) {
        alert("Appointment booked successfully!");
      } else {
        console.error("Error booking appointment:", response.statusText);
      }
    } catch (error) {
      console.error("Error booking appointment:", error);
    }
  };

  return (
    <div className="book-appointment">
      <h2>Book Appointment</h2>

      {!userData && (
        <div>
          <input
            type="text"
            placeholder="Enter Phone Number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
          <button onClick={fetchUserData}>Fetch User</button>
        </div>
      )}

      {newUser && (
        <div>
          <input
            type="text"
            placeholder="Enter Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button onClick={saveNewUser}>Save User</button>
        </div>
      )}

      {userData && (
        <>
          <div>
            <label>Appointment Type</label>
            <select onChange={(e) => setAppointmentType(e.target.value)}>
              <option value="">Select</option>
              <option value="Tele Consultation">Tele Consultation</option>
              <option value="Direct Consultation">Direct Consultation</option>
            </select>
          </div>

          <div>
            <label>Department</label>
            <select
              onChange={(e) => {
                setDepartmentId(e.target.value);
                fetchPOCs(e.target.value);
              }}
            >
              <option value="">Select</option>
              {departments.map((dept) => (
                <option key={dept.id} value={dept.id}>
                  {dept.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>POC</label>
            <select
              onChange={(e) => {
                setPocId(e.target.value);
                fetchAvailableDates(e.target.value);
              }}
            >
              <option value="">Select</option>
              {pocs.map((poc) => (
                <option key={poc.id} value={poc.id}>
                  {poc.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Date</label>
            <select
              onChange={(e) => {
                setAppointmentDate(e.target.value);
                fetchAvailableTimes(pocId, e.target.value);
              }}
            >
              <option value="">Select</option>
              {dates.map((date) => (
                <option key={date} value={date}>
                  {date}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Time</label>
            <select onChange={(e) => setAppointmentTime(e.target.value)}>
              <option value="">Select</option>
              {times.map((time) => (
                <option key={time} value={time}>
                  {time}
                </option>
              ))}
            </select>
          </div>

          <button onClick={handleConfirm}>Confirm Appointment</button>
        </>
      )}
    </div>
  );
}

export default BookAppointment;
